class santa_climbing ():
    
    def __init__(self):
        self.floor = 0
    
    def move (self, char_input):
        if char_input == "(":
            self.floor += 1
        elif char_input == ")":
            self.floor -= 1
    
    def calculate_floor (self, string_input):
        for char in string_input:
            self.move(char)
        return self.floor
    
