class santa_climbing ():
    
    def __init__(self):
        self.floor = 0
        self.position = 0
    
    def move (self, char_input):
        if char_input == "(":
            self.floor += 1
        elif char_input == ")":
            self.floor -= 1
    
    def calculate_floor (self, string_input):
        counter = 1
        positions = []
        for char in string_input:
            self.move(char)
            if self.floor == -1:
                positions.append(counter)
            counter += 1
        if len(positions) > 0:
            self.position = positions[0]
        return self.floor
    
