import unittest
from main import santa_climbing


class Test(unittest.TestCase):
    
    def setUp(self):
        self.santa_class = santa_climbing()

    def tearDown(self):
        self.santa_class = None

    def test_empty(self):
        self.assertEqual(0, self.santa_class.calculate_floor(""))
    
    def test_zero (self):
        self.assertEqual(0, self.santa_class.calculate_floor("(())"))
    
    def test_zero_independent (self):
        self.assertEqual(0, self.santa_class.calculate_floor("()()"))
    
    def test_one (self):
        self.assertEqual(1, self.santa_class.calculate_floor("("))
    
    def test_three (self):
        self.assertEqual(3, self.santa_class.calculate_floor("((("))
        
    def test_three_mixed (self):
        self.assertEqual(3, self.santa_class.calculate_floor("(()(()("))
    
    def test_minus_one (self):
        self.assertEqual(-1, self.santa_class.calculate_floor("())"))
    
    def test_minus_one_mixed (self):
        self.assertEqual(-1, self.santa_class.calculate_floor("))("))
    
    def test_minus_three (self):
        self.assertEqual(-3, self.santa_class.calculate_floor(")))"))
        
    def test_minus_three_mixed (self):
        self.assertEqual(-3, self.santa_class.calculate_floor(")())())"))
    
